import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEdit, faTrashAlt } from '@fortawesome/free-solid-svg-icons';
import { Modal, ModalBody, ModalFooter, ModalHeader } from 'reactstrap';

const url="https://serviciofactura-development.herokuapp.com/api/v1/factura/";

class App extends Component {
state={
  data:[],
  modalInsertar: false,
  modalEliminar: false,
  form:{
    folio: '',
    razon_social_empresa: '',
    direccion: '',
    cp: '',
    tipoModal: ''
  }
}

peticionGet=()=>{
axios.get(url).then(response=>{
  this.setState({data: response.data.data});
}).catch(error=>{
  console.log(error.message);
})
}

peticionPost=async()=>{
  delete this.state.form.id;
 await axios.post(url,this.state.form).then(response=>{
    this.modalInsertar();
    this.peticionGet();
  }).catch(error=>{
    console.log(error.message);
  })
}

peticionPut=()=>{
  axios.put(url+this.state.form.folio, this.state.form).then(response=>{
  ;
    this.modalEditar();
    this.peticionGet();

  })
}

peticionDelete=()=>{
  axios.delete(url+this.state.form.folio).then(response=>{
    this.setState({modalEliminar: false});
    this.peticionGet();
  })
}

modalInsertar=()=>{
  this.setState({modalInsertar: !this.state.modalInsertar});
}

modalEditar=()=>{
  this.setState({modalEditar: !this.state.modalEditar});
}

seleccionarFactura=(factura)=>{
  this.setState({
    tipoModal: 'actualizar',
    form: {
      folio: factura.folio,
      razon_social_empresa:factura.razon_social_empresa,
      direccion: factura.direccion,
      cp: factura.cp,
      telefono: factura.telefono,
      rfc: factura.rfc,
      regimen_fiscal: factura.regimen_fiscal,
      fecha: factura.fecha,
      correo: factura.correo,
      folio_fiscal: factura.folio_fiscal,
      certificado_digital: factura.certificado_digital,
      serie_cerificado_SAT: factura.serie_cerificado_SAT,
      estado: factura.estado,
      idPago: factura.idPago,
      idCliente: factura.idCliente
    }
  })
}

handleChange=async e=>{
e.persist();
await this.setState({
  form:{
    ...this.state.form,
    [e.target.name]: e.target.value
  }
});
console.log(this.state.form);
}

  componentDidMount() {
    this.peticionGet();
  }


  render(){
    const {form}=this.state;
  return (
    <div className="App">
    <br /><br /><br />
  <button className="btn btn-success" onClick={()=>{this.setState({form: null, tipoModal: 'insertar'}); this.modalInsertar()}}>Crear factura</button>
  <br /><br />
    <table className="table ">
      <thead>
        <tr>
          <th>Folio</th>
          <th>Razon social</th>
          <th>Direccion</th>
          <th>Codigo postal</th>
          <th>Acciones</th>
        </tr>
      </thead>
      <tbody>
        {this.state.data.map(factura=>{
          return(
            <tr key={factura.folio}>
          <td>{factura.folio}</td>
          <td>{factura.razon_social_empresa}</td>
          <td>{factura.direccion}</td>
          <td>{factura.cp}</td>
          <td>
                <button className="btn btn-primary" onClick={()=>{this.seleccionarFactura(factura); this.modalEditar()}}><FontAwesomeIcon icon={faEdit}/></button>
                {"   "}
                <button className="btn btn-danger" onClick={()=>{this.seleccionarFactura(factura); this.setState({modalEliminar: true})}}><FontAwesomeIcon icon={faTrashAlt}/></button>

                </td>
          </tr>
          )
        })}
      </tbody>
    </table>



    <Modal isOpen={this.state.modalInsertar}>
                <ModalHeader style={{display: 'block'}}>
                  <span style={{float: 'right'}} onClick={()=>this.modalInsertar()}>x</span>
                </ModalHeader>
                <ModalBody>
                  <div className="form-group">
                    <label htmlFor="folio">Folio</label>
                    <input className="form-control" type="number" name="folio" id="folio" onChange={this.handleChange} value={form?form.folio: this.state.data.length+1}/>
                    <br />
                    <label htmlFor="folio_fiscal">Folio fiscal</label>
                    <input className="form-control" type="text" name="folio_fiscal" id="folio_fiscal" onChange={this.handleChange} value={form?form.folio_fiscal: ''}/>
                    <br />
                    <label htmlFor="idPago">id pago</label>
                    <input className="form-control" type="text" name="idPago" id="idPago" onChange={this.handleChange} value={form?form.idPago: ''}/>
                    <br />
                    <label htmlFor="idCliente">id cliente</label>
                    <input className="form-control" type="text" name="idCliente" id="idCliente" onChange={this.handleChange} value={form?form.idCliente:''}/>
                  </div>
                </ModalBody>

                <ModalFooter>
                  {this.state.tipoModal=='insertar'?
                    <button className="btn btn-success" onClick={()=>this.peticionPost()}>
                    Insertar
                  </button>: <button className="btn btn-primary" onClick={()=>this.peticionPut()}>
                    Actualizar
                  </button>
  }
                    <button className="btn btn-danger" onClick={()=>this.modalInsertar()}>Cancelar</button>
                </ModalFooter>
          </Modal>



          <Modal isOpen={this.state.modalEditar}>
                <ModalHeader style={{display: 'block'}}>
                  <span style={{float: 'right'}} onClick={()=>this.modalEditar()}>x</span>
                </ModalHeader>
                <ModalBody>
                <div className="form-group">
                    <label htmlFor="folio">Folio</label>
                    <input className="form-control" type="number" name="folio" id="folio" onChange={this.handleChange} value={form?form.folio: this.state.data}/>
                    <br />
                    <label htmlFor="razon_social_empresa">Razon social de la empresa</label>
                    <input className="form-control" type="text" name="razon_social_empresa" id="razon_social_empresa" onChange={this.handleChange} value={form?form.razon_social_empresa: this.state.data}/>
                    <br />
                    <label htmlFor="direccion">Direccion</label>
                    <input className="form-control" type="text" name="direccion" id="direccion" onChange={this.handleChange} value={form?form.direccion: this.state.data}/>
                    <br />
                    <label htmlFor="cp">Codigo postal</label>
                    <input className="form-control" type="text" name="cp" id="cp" onChange={this.handleChange} value={form?form.cp: this.state.data}/>
                    <br />
                    <label htmlFor="correo">Correo</label>
                    <input className="form-control" type="text" name="correo" id="correo" onChange={this.handleChange} value={form?form.correo: this.state.data}/>
                    <br />
                    <label htmlFor="telefono">Telefono</label>
                    <input className="form-control" type="text" name="telefono" id="telefono" onChange={this.handleChange} value={form?form.telefono: this.state.data}/>
                    <br />
                    <label htmlFor="rfc">rfc</label>
                    <input className="form-control" type="text" name="rfc" id="rfc" onChange={this.handleChange} value={form?form.rfc: this.state.data}/>
                    <br />
                    <label htmlFor="regimen_fiscal">Regimen fiscal</label>
                    <input className="form-control" type="text" name="regimen_fiscal" id="regimen_fiscal" onChange={this.handleChange} value={form?form.regimen_fiscal: this.state.data}/>
                    <br />
                    <label htmlFor="fecha">Fecha</label>
                    <input className="form-control" type="text" name="fecha" id="fecha" onChange={this.handleChange} value={form?form.fecha: this.state.data}/>
                    <br />
                    <label htmlFor="folio_fiscal">Folio fiscal</label>
                    <input className="form-control" type="text" name="folio_fiscal" id="folio_fiscal" onChange={this.handleChange} value={form?form.folio_fiscal: this.state.data}/>
                    <br />
                    <label htmlFor="certificado_digital">Certificado digital</label>
                    <input className="form-control" type="text" name="certificado_digital" id="certificado_digital" onChange={this.handleChange} value={form?form.certificado_digital: this.state.data}/>
                    <br />
                    <label htmlFor="serie_cerificado_SAT">Serie cerificado del SAT digital</label>
                    <input className="form-control" type="text" name="serie_cerificado_SAT" id="serie_cerificado_SAT" onChange={this.handleChange} value={form?form.serie_cerificado_SAT: this.state.data}/>
                    <br />
                    <label htmlFor="estado">Estado</label>
                    <input className="form-control" type="text" name="estado" id="estado" onChange={this.handleChange} value={form?form.estado: this.state.data}/>
                    <br />
                    <label htmlFor="idPago">id Pago</label>
                    <input className="form-control" type="text" name="idPago" id="idPago" onChange={this.handleChange} value={form?form.idPago: this.state.data}/>
                    <br />
                    <label htmlFor="idCliente">id cliente</label>
                    <input className="form-control" type="text" name="idCliente" id="idCliente" onChange={this.handleChange} value={form?form.idCliente:this.state.data}/>
                  </div>
                </ModalBody>

                <ModalFooter>
                  {this.state.tipoModal=='insertar'?
                    <button className="btn btn-success" onClick={()=>this.peticionPost()}>
                    Insertar
                  </button>: <button className="btn btn-primary" onClick={()=>this.peticionPut()}>
                    Actualizar
                  </button>
  }
                    <button className="btn btn-danger" onClick={()=>this.modalEditar()}>Cancelar</button>
                </ModalFooter>
          </Modal>


          <Modal isOpen={this.state.modalEliminar}>
            <ModalBody>
               Estás seguro que deseas eliminar la factura {form && form.folio}
            </ModalBody>
            <ModalFooter>
              <button className="btn btn-danger" onClick={()=>this.peticionDelete()}>Sí</button>
              <button className="btn btn-secundary" onClick={()=>this.setState({modalEliminar: false})}>No</button>
            </ModalFooter>
          </Modal>
  </div>



  );
}
}
export default App;
